<script>
import { mapGetters } from 'vuex'
import host from '../../AxiosMethods/index.js'
import Cookies from 'js-cookie';
import MenuAddItem from './MenuAddItem.vue';
export default {
	data() {
		return {
			SettingsWindow: {
				activeCart: false,
			},
			authMenu: {
				active: false,
				login: undefined,
				password: undefined,
			},
			registrationMenu: {
				active: false,
				login: undefined,
				password: undefined,
				phoneNumber: undefined,
				name: undefined,
			},
			selectedAddedItem: null
		};
	},
	components: { MenuAddItem },
	computed: {
		...mapGetters({
			cartItems: 'getCartUser'
		}),
		getCattegoryRealName() {
			return (cattegory) => {
				switch (cattegory) {
					case "Snack":
						return "Закуски";
					case "Pizza":
						return "Пиццы";
					case "Drink":
						return "Напитки";
					case "Coctail":
						return "Коктейли";
					case "Coffee":
						return "Кофе";
					case "Dessert":
						return "Дессерты";
					case "Souse":
						return "Соусы";
				}
			}
		},
		getButtonText() {
			return (item) => {
				switch (item.Cattegory) {
					case "Pizza":
						return "Выбрать";
					case "Snack":
					case "Drink":
					case "Coctail":
					case "Coffee":
					case "Dessert":
					case "Souse":
						if (this.$store.state.loggedUser) {
							const cartItems = this.$store.state.loggedUser.CartItems;
							const findIndex = cartItems.findIndex(_ => _.CategoryType == item.Cattegory && _.UUID == item.UUID);
							if (findIndex !== -1) {
								return {
									Count: cartItems[findIndex].Count
								};
							}
						}
						return `Добавить`;
				}
			}
		},
		getCattegoryList() {
			const cattegoryList = [];
			this.$store.state.MainMenu.ItemsList.forEach(el => {
				if (cattegoryList.indexOf(el.Cattegory) == -1) {
					cattegoryList.push(el.Cattegory);
				}
			});
			return cattegoryList;
		},
		getItemsCattegory() {
			return (cattegoryName) => {
				return this.$store.state.MainMenu.ItemsList.filter(_ => _.Cattegory == cattegoryName);
			}
		}
	},
	methods: {
		openMaps() {
			window.open("https://www.google.com/maps/dir/?api=1&origin=улица Алибегова, дом 12, Минск, Беларусь&destination=улица Пушкина, дом 5, Москва, Россия&waypoints=улица Ленина, дом 10, Киев, Украина|улица Мира, дом 15, Варшава, Польша");
		},
		closeItemMenu() {
			this.selectedAddedItem = null;
		},
		openAuthorizationOnRegister() {
			this.clearRegistrationMenu();
			this.authMenu.active = true;
		},
		clearRegistrationMenu() {
			this.registrationMenu.active = false;
			this.registrationMenu.login = undefined;
			this.registrationMenu.password = undefined;
			this.registrationMenu.phoneNumber = undefined;
			this.registrationMenu.name = undefined;
		},
		clearAndCloseAuthMenu() {
			this.authMenu.active = false;
			this.authMenu.login = undefined;
			this.authMenu.password = undefined;
		},
		openRegisterMenu() {
			this.clearAndCloseAuthMenu();
			this.registrationMenu.active = true
		},
		async openAuthMenu() {
			if (this.$methods.getLoggedInAccount()) {
				const result = await host.get("/ExitAccount", {
					params: {
						Cookie: document.cookie
					}
				});
				if (result && result.data) {
					const response = result.data;
					switch (response.Result) {
						case "Success":
							Cookies.remove("login");
							Cookies.remove("hash");
							this.$store.commit("setLoggedUser", null);
							break;
					}
					this.emitter.emit("Notify:Push", {
						Title: response.Result == "Error" ? "Ошибка" : "Успешно",
						Message: response.Notify,
						Time: 2500
					});
				}
				return;
			}
			this.authMenu.active = true;
			document.body.style.overflow = 'hidden';
		},
		closeAuthMenu() {
			this.authMenu.active = false;
			document.body.style.overflow = 'auto';
		},
		scrollToCattegory(cattegory) {
			const divElement = document.getElementById(`categories_item_${cattegory}`);
			if (divElement) {
				divElement.scrollIntoView({ behavior: "smooth" });
			}
		},
		async setItemCountWithData(item, value) {
			if (!this.$methods.getLoggedInAccount()) {
				this.emitter.emit("Notify:Push", {
					Title: "Ошибка",
					Message: "Вы не вошли в аккаунт !",
					Time: 2500
				});
				return;
			}
			const cattegoryType = item.CategoryType,
				uuidItem = item.UUID;
			if (cattegoryType != "Pizza") {
				this.setItemCount(cattegoryType, uuidItem, value);
				return;
			}
			const result = await host.get("SetValueItemInCart", {
				params: {
					CattegoryType: cattegoryType,
					ItemUUID: uuidItem,
					Login: this.$store.state.loggedUser.Login,
					PhoneNumber: this.$store.state.loggedUser.PhoneNumber,
					Value: value,
					ItemData: item.Settings
				}
			});
			if (result && result.data) {
				const response = result.data;
				switch (response.Result) {
					case "Success":
						this.$store.commit("setLoggedUserCartItems", response.CartList);
						break;
				}
				this.emitter.emit("Notify:Push", {
					Title: response.Result == "Error" ? "Ошибка" : "Успешно",
					Message: response.Notify,
					Time: 2500
				});
			}
		},
		async setItemCount(cattegoryType, uuidItem, value) {
			if (!this.$methods.getLoggedInAccount()) {
				this.emitter.emit("Notify:Push", {
					Title: "Ошибка",
					Message: "Вы не вошли в аккаунт !",
					Time: 2500
				});
				return;
			}
			switch (cattegoryType) {
				case "Pizza":
					return;
				case "Snack":
				case "Drink":
				case "Coctail":
				case "Coffee":
				case "Dessert":
				case "Souse":
					break;
			}
			const result = await host.get("SetValueItemInCart", {
				params: {
					CattegoryType: cattegoryType,
					ItemUUID: uuidItem,
					Login: this.$store.state.loggedUser.Login,
					PhoneNumber: this.$store.state.loggedUser.PhoneNumber,
					Value: value
				}
			});
			if (result && result.data) {
				const response = result.data;
				switch (response.Result) {
					case "Success":
						this.$store.commit("setLoggedUserCartItems", response.CartList);
						break;
				}
				this.emitter.emit("Notify:Push", {
					Title: response.Result == "Error" ? "Ошибка" : "Успешно",
